      respawnSound.currentTime = 0;
      respawnSound.play();
    }

    function popBubble(e) {
      const x = e.clientX;
      const y = e.clientY;

      bubbles.forEach((bubble, index) => {
        const bubbleX = bubble.offsetLeft + 25;
        const bubbleY = bubble.offsetTop + 25;
        const distance = Math.sqrt(Math.pow(x - bubbleX, 2) + Math.pow(y - bubbleY, 2));

        if (distance < 25) {
          if (index === toughBubbleIndex) {
            toughBubbleClicks++;
            console.log("Tough bubble clicks:", toughBubbleClicks);
            if (toughBubbleClicks >= 10) {
              bubble.remove();
              bubbles.splice(index, 1);
              playPopSound();
              toughBubbleIndex = -1; // Reset the tough bubble index
              toughBubbleClicks = 0;
            } else {
              return; // Don't pop yet
            }
          } else {
            bubble.remove();
            bubbles.splice(index, 1);
            playPopSound();
          }

          if (bubbles.length === 0) {
            showMessage();
            playRespawnSound();
            setTimeout(() => {
              hideMessage();
              createBubbles();
            }, 2000);
          } else {
            createBubble(); // Only create one bubble if not empty
            if(toughBubbleIndex === -1) {
              selectToughBubble();
            }
          }
        }
      });
    }

    function createBubble() {
      const bubble = document.createElement("div");
      bubble.classList.add("bubble");
      bubble.style.left = Math.random() * (window.innerWidth - 50) + "px";
      bubble.style.top = window.innerHeight + 50 + "px";
      bubble.style.animationDuration = Math.random() * 3 + 2 + "s";
      document.body.appendChild(bubble);
      bubbles.push(bubble);
    }

    function repelBubbles() {
      bubbles.forEach(bubble => {
        const bubbleX = bubble.offsetLeft + 25;
        const bubbleY = bubble.offsetTop + 25;
        const dx = bubbleX - mouseX;
        const dy = bubbleY - mouseY;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < 100) {
          const angle = Math.atan2(dy, dx);
          const force = 5;
          const moveX = Math.cos(angle) * force;
          const moveY = Math.sin(angle) * force;

          bubble.style.transform = `translate(${moveX}px, ${moveY}px)`;
        } else {
          bubble.style.transform = 'translate(0, 0)';
        }
      });
    }

    function showMessage() {
      messageElement.style.display = "block";
    }

    function hideMessage() {
      messageElement.style.display = "none";
    }

    document.addEventListener('mousemove', (e) => {
      mouseX = e.clientX;
      mouseY = e.clientY;
    });

    document.addEventListener("click", popBubble);

    function animate() {
      repelBubbles();
      requestAnimationFrame(animate);
    }

    //Initial creation and sound on load
    playRespawnSound();
    createBubbles();

    animate();
  </script>
</body>
</html>
```

Key changes and explanations:

*   **`toughBubbleIndex` and `toughBubbleClicks`:** These variables track the index of the special bubble that requires multiple clicks and the number of clicks it has received.  `toughBubbleIndex` is initialized to `-1` to indicate that no tough bubble has been assigned yet.
*   **`selectToughBubble()` Function:** This function randomly selects a bubble from the `bubbles` array and assigns its index to `toughBubbleIndex`.  It also resets `toughBubbleClicks` to 0. This function is called after respawning bubbles or creating a new one.
*   **Modified `popBubble()` Function:**
    *   The `popBubble` function now checks if the clicked bubble's index matches `toughBubbleIndex`.
    *   If it's the tough bubble, `toughBubbleClicks` is incremented. If the click count reaches 10 or more, the bubble is removed, `toughBubbleIndex` is reset to `-1`, and the counter is also reset. The `return` statement prevents the standard popping behavior when the tough bubble isn't ready to pop.
    *   If it's *not* the tough bubble, the bubble pops as normal.
*   **Ensuring a new Tough Bubble is Selected:** Added `if(toughBubbleIndex === -1) { selectToughBubble(); }` in two places to make sure a new tough bubble is selected:  After the tough bubble is successfully popped, and after respawning or creating new bubbles. This is crucial, so the user always has a tough bubble to try popping.

This code now implements the required behavior: one random bubble requires 10 clicks to pop. The `console.log` statements are helpful for debugging and understanding the logic.

