    <script>
        let scene, camera, renderer, handyc, head;
        let direction = new THREE.Vector3();
        let speed = 0.02;

        function init() {
            // Create scene
            scene = new THREE.Scene();
            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
