            document.getElementById('risks').innerHTML = '';
            risks.forEach(risk => {
                const li = document.createElement('li');
                li.textContent = risk;
                document.getElementById('risks').appendChild(li);
            });

            // Estimate company revenue
            const averagePricePerGram = 0.005; // Price in dollars
