    <script>
        const serverUrl = 'wss://irc.h4ks.com:8097';
        const randomNumber = Math.floor(Math.random() * 1000);
        const nick = `_robot_bean_score_fetcher_${randomNumber}`;
        let responseReceived = false;

        const ws = new WebSocket(serverUrl);

        ws.onopen = () => {
            console.log("Connected to the server");

            // Send the NICK and USER commands
            ws.send(`NICK ${nick}`);
            ws.send(`USER ${nick} 0 * :${nick}`);
            ws.send(`PONG ${serverUrl}`);

            // Automatically send the command to fetch beans score
            ws.send(`PRIVMSG _cloudbot :.topbeans`);

            // Start progress bar animation
            document.getElementById('progress').style.width = '100%';
        };

        ws.onmessage = (event) => {
            const message = event.data;

            // Log the received message
            console.log("Received message:", message);

            // Check if the message starts with ":_cloudbot!"
            if (message.startsWith(":_cloudbot!")) {
                // Use regex to capture everything after the second colon
                const regex = /^:[^:]*:(.*)/;
                const match = message.match(regex);

                if (match && match[1]) {
                    const content = match[1].trim();
                    console.log("Content extracted:", content); // Log extracted content
                    displayOutput(content);
                } else {
                    console.error("Regex did not capture content correctly."); // Log error
                    displayOutput("Nothing found");
                }
                responseReceived = true; // Mark that we have received a response
            }
        };

        function displayOutput(message) {
            const outputDiv = document.getElementById('output');
            outputDiv.innerHTML = ""; // Clear previous output

            // Parse the message into a table, matching the specified format
            const listItems = message.split(" | ");
            const table = document.createElement("table");

            // Create table header
            const headerRow = document.createElement("tr");
            headerRow.innerHTML = "<th>Rank</th><th>User</th><th>Beans</th>";
            table.appendChild(headerRow);

            for (const item of listItems) {
                console.log("Parsing item:", item); // Log each item being parsed

                // Adjusted regex to extract rank, user, and beans, ignoring emojis
                const parts = item.match(/^(\d+)\.\s*([\w]+)\s*\(.*?(\d+)\s*beans\)/);
                if (parts) {
                    const row = document.createElement("tr");
                    // Create cells for rank, user, and bean count
                    row.innerHTML = `<td>${parts[1]}</td><td>${parts[2]}</td><td>${parts[3]}</td>`;
                    table.appendChild(row);
                } else {
                    console.error("Failed to match parts for item:", item); // Log error if no match
                }
            }

            outputDiv.appendChild(table); // Append the table to the output div
            outputDiv.style.display = 'block'; // Show output
            document.getElementById('loading').style.display = 'none'; // Hide loading bar
        }

        // Set a timeout to check for response
        setTimeout(() => {
            if (!responseReceived) {
                displayOutput("Failed to fetch");
            }
        }, 15000); // 15 seconds timeout
    </script>
