Please use the following code as a base to update:
```
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0"/>
    <title>Bean Market Cap</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            color: #333;
            margin: 0;
            padding: 10px;
            height: 100vh;
            display: flex;
            align-items: center;
            justify-content: center;
            position: relative;
            overflow: hidden;
        }

        body::before {
            content: "";
            position: fixed;
            top: 0; left: 0; right: 0; bottom: 0;
            background: url('https://www.everwilde.com/media/0800/VBESPIN-A-Pinto-Dry-Bean-Seeds.jpg') no-repeat center center fixed;
            background-size: cover;
            filter: blur(8px);
            z-index: -1;
        }

        #dashboard {
            background: rgba(255, 255, 255, 0.9);
            border-radius: 10px;
            box-shadow: 0 0 15px rgba(0, 0, 0, 0.2);
            padding: 20px;
            text-align: center;
            width: 90%;
            max-width: 400px;
            height: 80vh;
            position: relative;
            z-index: 1;
            overflow-y: auto;
        }

        h1 {
            margin: 0;
            font-size: 2em;
            color: #388e3c;
        }

        #loading {
            width: 100%;
            height: 20px;
            border-radius: 5px;
            background-color: #a5d6a7;
            overflow: hidden;
            position: relative;
            margin-top: 20px;
        }

        #progress {
            height: 100%;
            width: 0%;
            background-color: #76c7c0;
            transition: width 0.5s; /* Shorter transition for immediate completion */
        }

        #output {
            margin-top: 20px;
            padding: 10px;
            background: #f1f8e9;
            border-radius: 5px;
            display: none;
        }

        table {
            width: 100%;
            border-collapse: collapse;
            margin-top: 15px;
        }

        th, td {
            padding: 8px;
            text-align: left;
            cursor: pointer; /* Indicate that the header is clickable */
        }

        th {
            background-color: #76c7c0;
            color: white;
        }

        tr:nth-child(even) {
            background-color: #e0f2f1;
        }
    </style>
</head>
<body>
    <div id="dashboard">
        <h1>üèÜ Bean Market Cap</h1>
        <div id="loading">
            <div id="progress"></div>
        </div>
        <div id="output"></div>
    </div>

    <script>
        const serverUrl = 'wss://irc.h4ks.com:8097';
        const randomNumber = Math.floor(Math.random() * 1000);
        const nick = `_robot_bean_score_fetcher_${randomNumber}`;
        let ws;

        function connectWebSocket() {
            ws = new WebSocket(serverUrl);

            ws.onopen = () => {
                console.log("Connected to IRC");

                ws.send(`NICK ${nick}`);
                ws.send(`USER ${nick} 0 * :${nick}`);
                sendTopBeans(); // Immediate fetch
                setInterval(sendTopBeans, 10000); // Re-fetch every 10s
            };

            ws.onmessage = (event) => {
                const message = event.data;
                console.log("IRC Message:", message);

                // Respond to PINGs
                if (message.startsWith('PING')) {
                    const response = message.replace('PING', 'PONG');
                    ws.send(response);
                    console.log('Responded to PING:', response);
                    return;
                }

                // Handle scoreboard messages from _cloudbot
                if (message.startsWith(":_cloudbot!")) {
                    const regex = /^:[^:]*:(.*)/;
                    const match = message.match(regex);

                    if (match && match[1]) {
                        displayOutput(match[1].trim());
                    } else {
                        displayOutput("Nothing found");
                    }
                }
            };

            ws.onerror = (err) => {
                console.error("WebSocket error:", err);
            };

            ws.onclose = () => {
                console.warn("WebSocket closed, reconnecting in 5s...");
                setTimeout(connectWebSocket, 5000);
            };
        }

        function sendTopBeans() {
            if (ws && ws.readyState === WebSocket.OPEN) {
                ws.send(`PRIVMSG _cloudbot :.topbeans`);
                animateProgressBar();
            }
        }

        function animateProgressBar() {
            const progress = document.getElementById('progress');
            progress.style.transition = 'none';
            progress.style.width = '0%';
            requestAnimationFrame(() => {
                progress.style.transition = 'width 0.5s'; // Short transition for immediate completion
                progress.style.width = '100%';
            });
        }

        function displayOutput(message) {
            const outputDiv = document.getElementById('output');
            outputDiv.innerHTML = "";
            const listItems = message.split(" | ");
            const table = document.createElement("table");

            const headerRow = document.createElement("tr");
            headerRow.innerHTML = "<th onclick='sortTable(0)'>Rank</th><th onclick='sortTable(1)'>User</th><th>Beans</th>";
            table.appendChild(headerRow);

            const rows = [];
            for (const item of listItems) {
                const parts = item.match(/(\d+)\.\s+([\w\/]+).*\((\d+)\s+beans\)/);
                if (parts) {
                    const row = document.createElement("tr");
                    row.innerHTML = `<td>${parts[1]}</td><td>${parts[2]}</td><td>${parts[3]}</td>`;
                    rows.push(row);
                }
            }

            rows.forEach(row => table.appendChild(row));
            outputDiv.appendChild(table);
            outputDiv.style.display = 'block';
            document.getElementById('loading').style.display = 'block';
            document.getElementById('progress').style.width = '100%'; // Complete the progress bar immediately
        }

        let sortOrder = [true, true]; // [rankOrder, userOrder]

        function sortTable(columnIndex) {
            const table = document.getElementById('output').querySelector('table');
            const rows = Array.from(table.rows).slice(1); // Exclude header row

            rows.sort((a, b) => {
                const aText = a.cells[columnIndex].innerText;
                const bText = b.cells[columnIndex].innerText;

                if (columnIndex === 0) { // Rank column
                    return sortOrder[0] ? aText - bText : bText - aText;
                } else if (columnIndex === 1) { // User column
                    return sortOrder[1] ? aText.localeCompare(bText) : bText.localeCompare(aText);
                }
            });

            // Toggle sort order for the next click
            sortOrder[columnIndex] = !sortOrder[columnIndex];

            // Clear the table and append sorted rows
            while (table.rows.length > 1) {
                table.deleteRow(1);
            }
            rows.forEach(row => table.appendChild(row));
        }

        connectWebSocket();
    </script>
</body>
</html>

