            const formula = document.getElementById('formula').value;
            if (!isPlaying) {
                audioContext = new (window.AudioContext || window.webkitAudioContext)();
                gainNode = audioContext.createGain();
                gainNode.gain.value = document.getElementById('volume').value / 100; // Set initial volume
                oscillator = audioContext.createScriptProcessor(4096, 1, 1);
                oscillator.onaudioprocess = (e) => {
                    const output = e.outputBuffer.getChannelData(0);
                    for (let i = 0; i < output.length; i++) {
                        const t = audioContext.currentTime * 44100 + i;
                        output[i] = eval(formula);
