<!DOCTYPE html>
<html>
<head>
<title>Random Characters</title>
<style>
body {
  font-family: monospace;
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-color: #f0f0f0;
}

.container {
  text-align: center;
}

.line {
  margin: 5px 0; /* Add some vertical spacing between lines */
  white-space: nowrap; /* Prevent line breaks */
}

.char {
  margin-right: 30px; /* Add spacing between characters */
  display: inline-block; /* Make sure the margins work correctly */
}
</style>
</head>
<body>

<div class="container">
  <div id="charLines"></div>
</div>

<script>
function generateRandomChars() {
  const charSet = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789-.";
  let lines = [5, 6, 7, 8, 9, 8, 7, 6, 5];
  let allChars = "";

  for (let i = 0; i < lines.reduce((a, b) => a + b, 0); i++) {
      allChars += charSet.charAt(Math.floor(Math.random() * charSet.length));
  }

  let charLinesHTML = "";
  let startIndex = 0;
  for (let i = 0; i < lines.length; i++) {
      let lineLength = lines[i];
      let lineChars = allChars.substring(startIndex, startIndex + lineLength);
      let lineHTML = "";
      for (let j = 0; j < lineChars.length; j++) {
          lineHTML += `<span class="char">${lineChars[j]}</span>`;
      }
      charLinesHTML += `<div class="line">${lineHTML}</div>`;
      startIndex += lineLength;
  }

  document.getElementById("charLines").innerHTML = charLinesHTML;
}

generateRandomChars();
</script>

</body>
</html>
