<!DOCTYPE html>
<html>
<head>
<title>Rotating Colored Circles</title>
<style>
  body {
    margin: 0;
    overflow: hidden;
    background-color: #000;
  }

  .circle {
    position: absolute;
    border-radius: 50%;
    animation: rotate 0.1s linear infinite;
  }

  @keyframes rotate {
    from {
      transform: rotateZ(0deg);
    }
    to {
      transform: rotateZ(360deg);
    }
  }
</style>
</head>
<body>

<script>
  function getRandomColor() {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  }

  function getRandomPosition() {
    const x = Math.random() * window.innerWidth;
    const y = Math.random() * window.innerHeight;
    return { x, y };
  }

  function createCircle() {
    const circle = document.createElement('div');
    circle.classList.add('circle');

    const size = Math.random() * 50 + 20; // Random size between 20 and 70 pixels
    circle.style.width = size + 'px';
    circle.style.height = size + 'px';

    const { x, y } = getRandomPosition();
    circle.style.left = x - (size / 2) + 'px'; // Center the circle
    circle.style.top = y - (size / 2) + 'px';   // Center the circle

    circle.style.backgroundColor = getRandomColor();
    circle.style.zIndex = Math.floor(Math.random() * 100); // Random z-index for layering

    document.body.appendChild(circle);
  }

  function populateCircles(numCircles) {
    for (let i = 0; i < numCircles; i++) {
      createCircle();
    }
  }

  // Initial population of circles (adjust number as needed)
  populateCircles(25);

  // Add new circles periodically (optional, but keeps things interesting)
  setInterval(() => {
      createCircle();
  }, 1000);  // Create a new circle every 1 second.  Adjust as needed.
</script>

</body>
</html>
