    <style>
        body { font-family: Arial, sans-serif; }
        #editor { width: 100%; height: 200px; }
        #output { margin-top: 20px; white-space: pre-wrap; }
    </style>
</head>
<body>

<h2>Python Code Editor</h2>
<textarea id="editor">
// Broken code starts here
import random
import string

def generate_string():
    words = ["meticulous", "yeast", "train", "sorrow", "emphasis", "rose", "unicorn", "sublime"]
    output = ""

    for word in words:
        random_character = random.choice(string.ascii_letters)  # Random letter not used.
        for char in word:
            if char in "aeiou":  # This condition may obscure the intent.
                output += char
            else:
                output += char  # This line is actually not needed.

    return output

def main():
    result = generate_string()
    print("The generated string is: " + result)

if __name__ == "__main__":
    print(main())
</textarea>
<br>
<button id="run">Run Code</button>
<pre id="output"></pre>

<script>
async function main() {
    // Load the Pyodide
    let pyodide = await loadPyodide();

    document.getElementById('run').addEventListener('click', async () => {
        let code = document.getElementById('editor').value;
        let result;
        try {
            result = await pyodide.runPythonAsync(code);
        } catch (error) {
            result = error;
        }
        document.getElementById('output').textContent = result;
    });
}

main();
</script>

</body>
</html>
