                text.setAttribute('color', '#FFFFFF'); // White text color

                // Store cubes in an array
                cubes.push({ box, text });

                // Append the cube and text to the container
                container.appendChild(box);
                container.appendChild(text);
            }
        }

        // Function to update cubes' labels and JSON display
        async function updateCubes() {
            const jsonData = await fetchJson(); // Fetch new JSON data

            if (jsonData) {
                jsonDisplay.innerText = JSON.stringify(jsonData, null, 2); // Update displayed JSON

                const colors = jsonData.colors || [];
                const positions = jsonData.positions || [];
                const labels = jsonData.randomStrings || [];

                // Update cubes
