                    const color = colors[index % colors.length] || '#FFFFFF'; // Fallback color
                    const pos = positions[index % positions.length] || { x: 0, y: 1, z: -5 }; // Fallback position
                    const label = labels[index % labels.length] || 'Label'; // Fallback label

                    cube.box.setAttribute('color', color); // Update cube color
