            }

            // Schedule updates every second
            setInterval(async () => {
                await updateCubes();
            }, 1000);

            // Schedule to fetch new data every 100 seconds
            setInterval(async () => {
                await fetchJson(); // Fetch new data
            }, 100000); // 100 seconds
        }

        // Start the application
        init();
    </script>
</body>
</html>
