    </a-scene>

    <script>
        const container = document.getElementById('scene-container');
        const jsonDisplay = document.getElementById('json-display');
        const spheres = []; // Array to hold sphere objects
        let currentJsonData; // Variable to hold current JSON data

        // Function to fetch random JSON data from the API
        async function fetchJson() {
            try {
                const response = await fetch('https://games.h4ks.com/game/dudeapi');
                const data = await response.json();
                return data; // Returns the whole JSON object
            } catch (error) {
                console.error('Error fetching data:', error);
                return null; // Return null in case of an error
            }
        }

        // Function to generate a random JSON formatted string
        function generateRandomJson() {
            const colors = Array.from({ length: 10 }, () => getRandomColor());
            const randomStrings = Array.from({ length: 10 }, () => getRandomLabel(5));
            return JSON.stringify({ colors, randomStrings });
        }

        // Function to generate a random color
        function getRandomColor() {
            const letters = "0123456789ABCDEF";
            let color = "#";
            for (let i = 0; i < 6; i++) {
                color += letters[Math.floor(Math.random() * 16)];
            }
            return color;
        }

        // Function to generate a random string of specified length
        function getRandomLabel(length) {
            const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
            let label = '';
            for (let i = 0; i < length; i++) {
                const randomIndex = Math.floor(Math.random() * characters.length);
                label += characters[randomIndex];
            }
            return label;
        }

        // Function to create buildings (rectangular objects)
        function createBuildings(jsonData) {
            const data = jsonData;

            for (let i = 0; i < 10; i++) {
                const color = data.colors[i] || '#FFFFFF'; // Fallback color
                const height = Math.random() * 2 + 0.5; // Random height between 0.5 to 2.5
                const width = Math.random() * 2 + 0.5; // Random width between 0.5 to 2.5
                const depth = Math.random() * 2 + 0.5; // Random depth between 0.5 to 2.5
                const pos = {
                    x: (Math.random() * 10 - 5).toFixed(2), // Random x position
                    y: height / 2, // Set y position to half of height for proper alignment
                    z: (Math.random() * -20).toFixed(2) // Random z position
                };

                // Create the rectangular building
                const box = document.createElement('a-box');
                box.setAttribute('position', `${pos.x} ${pos.y} ${pos.z}`);
                box.setAttribute('color', color);
                box.setAttribute('depth', depth);
                box.setAttribute('height', height);
                box.setAttribute('width', width);

                // Append the building to the container
                container.appendChild(box);
            }
        }

        // Function to create spheres based on JSON data
        function createSpheres(jsonData) {
            const data = JSON.parse(jsonData);

            for (let i = 0; i < 10; i++) {
                const color = getRandomColor(); // Random color for each sphere
                const pos = {
                    x: (Math.random() * 10 - 5).toFixed(2), // Random x position
                    y: 1, // Fixed y position
                    z: (Math.random() * -10).toFixed(2) // Random z position
                };
                const label = data.randomStrings[i] || 'Label'; // Use label from JSON

                // Create the sphere
                const sphere = document.createElement('a-sphere');
                sphere.setAttribute('position', `${pos.x} ${pos.y} ${pos.z}`);
                sphere.setAttribute('color', color);
                sphere.setAttribute('radius', '0.1'); // Very small sphere

                // Create the text label
                const text = document.createElement('a-text');
                text.setAttribute('value', label);
                text.setAttribute('position', `${pos.x} ${pos.y + 0.15} ${pos.z}`); // Position text above the sphere
                text.setAttribute('color', '#FFFFFF'); // White text color

                // Append the sphere and text to the container
                container.appendChild(sphere);
                container.appendChild(text);
            }
        }

        // Function to update spheres' labels and JSON display
        function updateSpheres() {
            const jsonData = JSON.stringify(currentJsonData, null, 2); // Display JSON data
            jsonDisplay.innerText = jsonData; // Update displayed JSON

            // Update the spheres
            const data = currentJsonData;
            if (data) {
                spheres.forEach((sphere, index) => {
                    const color = getRandomColor(); // Generate a new random color for the sphere
                    const label = data.randomStrings[index] || 'Label'; // Use label from JSON

                    sphere.sphere.setAttribute('color', color); // Update sphere color
                    sphere.text.setAttribute('value', label); // Update text label
                });
            }
        }

        // Set random sky and floor colors when the scene loads
        document.addEventListener('DOMContentLoaded', () => {
            const sky = document.getElementById('random-sky');
            sky.setAttribute('color', getRandomColor());

            const floor = document.getElementById('floor');
            floor.setAttribute('color', getRandomColor());
        });

        // Initialize the scene
        async function init() {
            currentJsonData = await fetchJson(); // Fetch initial random JSON data
            if (currentJsonData) {
                createBuildings(currentJsonData); // Create buildings based on fetched JSON data
                createSpheres(currentJsonData); // Create spheres based on fetched JSON data
                jsonDisplay.innerText = JSON.stringify(currentJsonData, null, 2); // Display initial JSON data
            }

            // Update JSON data every 10 seconds
            setInterval(async () => {
                currentJsonData = await fetchJson(); // Fetch new JSON data
            }, 10000);

            // Update spheres and JSON display every second
            setInterval(updateSpheres, 1000); // Update every second
        }

        // Start the application
        init();
    </script>
</body>
</html>
