            const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
            let result = '';
            for (let i = 0; i < length; i++) {
                result += characters.charAt(Math.floor(Math.random() * characters.length));
            }
            return result;
        }

        // Function to generate random data for spheres
        function generateRandomData() {
            const data = {
                colors: [],
                positions: [],
                labels: [],
            };

            for (let i = 0; i < 10; i++) {
                data.colors.push(getRandomColor());
                data.positions.push({
                    x: (Math.random() * 10 - 5).toFixed(2), // Range [-5, 5]
                    y: (Math.random() * 5 + 0.1).toFixed(2), // Range [0.1, 5]
                    z: (Math.random() * -10 - 1).toFixed(2), // Range [-10, -1]
                });
                data.labels.push(generateRandomString(5));
            }

            return data;
        }

        // Function to generate a random color
        function getRandomColor() {
            const letters = "0123456789ABCDEF";
            let color = "#";
