                building.setAttribute('position', `${(Math.random() * 10 - 5).toFixed(2)} ${height / 2} ${(-Math.random() * 10).toFixed(2)}`);
                building.setAttribute('color', getRandomColor());
                building.setAttribute('height', height);
                building.setAttribute('width', width);
                building.setAttribute('depth', depth);

                buildings.push(building);
                container.appendChild(building);
            }
        }

        // Function to create spheres based on JSON data
        function createSpheres(data) {
            for (let i = 0; i < 10; i++) {
                const pos = data.positions[i] || { x: 0, y: 1, z: -5 };
