            background-color: #f0f0f0;
        }
        canvas {
            display: block;
            background-color: #fff;
            border: 1px solid #ccc;
        }
        .score {
            position: absolute;
            top: 10px;
            left: 10px;
            font-size: 24px;
            font-weight: bold;
        }
    </style>
</head>
<body>
    <canvas id="gameCanvas"></canvas>
    <div class="score">Score: 0</div>
    <script>
        const canvas = document.getElementById('gameCanvas');
        const ctx = canvas.getContext('2d');
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight;

        let cakeRadius = 20;
        let cakeX = Math.random() * (canvas.width - 2 * cakeRadius) + cakeRadius;
        let cakeY = Math.random() * (canvas.height - 2 * cakeRadius) + cakeRadius;
        let cakeSpeedX = (Math.random() * 4 + 2) * (Math.random() > 0.5 ? 1 : -1);
        let cakeSpeedY = (Math.random() * 4 + 2) * (Math.random() > 0.5 ? 1 : -1);
        let score = 0;

        function drawCake() {
            ctx.beginPath();
            ctx.arc(cakeX, cakeY, cakeRadius, 0, Math.PI * 2);
            ctx.fillStyle = '#ffcc00';
            ctx.fill();
            ctx.closePath();

            // Draw "DVD" text
            ctx.fillStyle = '#000'; // Text color
            ctx.font = 'bold 16px Arial';
            ctx.textAlign = 'center';
            ctx.textBaseline = 'middle';
            ctx.fillText('DVD', cakeX, cakeY);
        }

        function update() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            drawCake();
            cakeX += cakeSpeedX;
            cakeY += cakeSpeedY;

            if (cakeX + cakeRadius > canvas.width || cakeX - cakeRadius < 0) {
                cakeSpeedX = -cakeSpeedX;
            }
            if (cakeY + cakeRadius > canvas.height || cakeY - cakeRadius < 0) {
                cakeSpeedY = -cakeSpeedY;
            }

            requestAnimationFrame(update);
        }

        canvas.addEventListener('click', function (event) {
            const mouseX = event.clientX;
            const mouseY = event.clientY;
            const distance = Math.sqrt((mouseX - cakeX) ** 2 + (mouseY - cakeY) ** 2);
            if (distance <= cakeRadius) {
                score++;
                const scoreDisplay = document.querySelector('.score');
                scoreDisplay.textContent = 'Score: ' + score;

                // Relocate the cake
                cakeX = Math.random() * (canvas.width - 2 * cakeRadius) + cakeRadius;
                cakeY = Math.random() * (canvas.height - 2 * cakeRadius) + cakeRadius;
            }
        });

        update();
    </script>
</body>
</html>
```

### Changes Made:
1. Added a `<div class="score">` to display the score on the screen.
2. Updated the `drawCake` function to include the text "DVD" drawn in the center of the cake circle.
3. Updated the scoring system to dynamically show the score in the designated area rather than using `alert`. 

You can copy and paste this code into an HTML file to run the bouncing cake minigame with "DVD" written inside the circle.

