            display: block;
            background-color: #fff;
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <canvas id="gameCanvas"></canvas>
    <script>
        const canvas = document.getElementById('gameCanvas');
        const ctx = canvas.getContext('2d');
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight;

        let balls = []; // Array to hold all balls

        function createBall(x, y) {
            const radius = 20;
            const speedX = (Math.random() * 4 + 2) * (Math.random() > 0.5 ? 1 : -1);
            const speedY = (Math.random() * 4 + 2) * (Math.random() > 0.5 ? 1 : -1);
            balls.push({ x, y, radius, speedX, speedY });
        }

        function drawBall(ball) {
            ctx.beginPath();
            ctx.arc(ball.x, ball.y, ball.radius, 0, Math.PI * 2);
            ctx.fillStyle = '#007bff'; // Blue color
            ctx.fill();
            ctx.closePath();

            // Draw "DVD" text inside the circle
            ctx.fillStyle = '#fff'; // Text color (white)
            ctx.font = 'bold 16px Arial';
            ctx.textAlign = 'center';
            ctx.textBaseline = 'middle';
            ctx.fillText('DVD', ball.x, ball.y);
        }

        function update() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            balls.forEach(ball => {
                drawBall(ball);
                ball.x += ball.speedX;
                ball.y += ball.speedY;

                // Check for collision with walls
                if (ball.x + ball.radius > canvas.width || ball.x - ball.radius < 0) {
