        }

        @keyframes backgroundAnimation {
            0% { background-color: #6a82fb; }
            100% { background-color: #fc5c7d; }
        }

        .container {
            text-align: center;
            background: rgba(255, 255, 255, 0.9);
            border-radius: 30px;
            box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
            padding: 50px;
