        .particle {
            position: absolute;
            border-radius: 50%;
            background: rgba(255, 255, 255, 0.8);
            animation: particleAnimation 5s infinite;
        }

        @keyframes particleAnimation {
            0% {
                transform: translateY(0) scale(1);
                opacity: 1;
            }
            100% {
                transform: translateY(-100vh) scale(0);
                opacity: 0;
            }
        }
    </style>
</head>
<body>
    <div class="container">
        <h1>Welcome</h1>
        <p>To a world of endless possibilities.</p>
        <button id="actionButton">Discover More</button>
    </div>

    <script>
        document.getElementById('actionButton').addEventListener('click', function() {
            alert('You clicked the button! What will you explore next?');
        });

        // Particle effect generation
        function createParticle() {
            const particle = document.createElement('div');
            particle.className = 'particle';
            const size = Math.random() * 10 + 5; // Random size between 5 and 15
            particle.style.width = `${size}px`;
            particle.style.height = `${size}px`;
            particle.style.left = `${Math.random() * 100}vw`;
            particle.style.animationDuration = `${Math.random() * 3 + 2}s`; // Random duration between 2s and 5s
            document.body.appendChild(particle);

            // Remove particle after animation
            particle.addEventListener('animationend', () => {
                particle.remove();
            });
        }

        // Create particles at intervals
        setInterval(createParticle, 300);
    </script>
</body>
</html>
