<script>
    function generateWords() {
        const input = document.getElementById('inputString').value.trim();
        if (!input) {
            alert('Please enter a word or phrase!');
            return;
        }

        // Use the first 10 characters of the input
        const baseString = input.substring(0, 10);

        // Generate 100 fantasy words
        let words = [];
        for (let i = 0; i < 100; i++) {
