            <p>${words.join(', ')}</p>
        `;
    }

    function generateFantasyWord(base) {
        const suffixes = ['na', 'or', 'il', 'en', 'am', 'os'];
        const randomSuffix = suffixes[Math.floor(Math.random() * suffixes.length)];

