<body>

<h1>Fantasy Language Creator</h1>
<p>Enter a base word or phrase to generate a fantasy vocabulary and grammar structure.</p>
<input type="text" id="inputString" placeholder="Enter a word or phrase" maxlength="200">
<label for="includeNumbers">
    <input type="checkbox" id="includeNumbers"> Include numbers (0-9)
</label>
<br>
<label for="rectangleCount">Number of rectangles on the flag:</label>
<input type="number" id="rectangleCount" value="2000" min="1" max="5000">
<br>
<button onclick="generateWords()">Generate Vocabulary</button>
<button onclick="generateGrammar()">Generate Chomsky Grammar</button>
<button onclick="generateNewGrammar()">Generate New Grammar</button>
<button onclick="generateRandomString()">Generate Random Source String (64 chars)</button>
<button onclick="generateExampleSentences()">Generate Example Sentences</button>

<div id="output"></div>

<script>
    let currentInput = '';
    let currentVocabulary = [];
    let currentGrammar = '';

    // Example translation mapping (placeholder translations)
    const translationMap = {
        "example1": "example one",
        "example2": "example two",
        "example3": "example three",
        "example4": "example four",
        "example5": "example five",
        "example6": "example six",
        "example7": "example seven",
        "example8": "example eight",
        "example9": "example nine",
        "example10": "example ten",
    };

    // Array of random English words (for demonstration)
    const randomWords = [
        "sunflower", "ocean", "mountain", "piano", "dance",
        "book", "computer", "journey", "forest", "flower",
        "star", "moon", "river", "cloud", "tree"
    ];

    // Definitions of various sentence structures
    const sentenceStructures = [
        'The {word1} is {word2}.',
        'A {word1} can {word2}.',
        'I saw a {word1} with a {word2}.',
        '{word1} is my {word2}.',
        'The {word2} is called {word1}.',
        'Where is the {word1}?',
        'I like the {word1}.',
        'The {word1} and {word2} are friends.',
        'What a beautiful {word1}!',
        'The {word1} went to find a {word2}.'
    ];

    function generateWords() {
        currentInput = document.getElementById('inputString').value.trim();
        if (!currentInput) {
            alert('Please enter a word or phrase!');
            return;
        }

        // Split the input into words by whitespace
        const wordsArray = currentInput.split(/\s+/);
        let words = [];

        // Generate up to 100 unique random substrings of valid length from the input string
        while (words.length < 100) {
            const randomStartIndex = Math.floor(Math.random() * currentInput.length);
            const randomLength = Math.floor(Math.random() * (15 - 1 + 1)) + 1; // Random length between 1 and 15
            const newWord = currentInput.substring(randomStartIndex, randomStartIndex + randomLength).trim();
            if (newWord.length <= 15 && newWord.length > 0 && !words.includes(newWord) && randomStartIndex + randomLength <= currentInput.length) {
                words.push(newWord); // Ensure uniqueness and valid length
            }
        }

        currentVocabulary = words;
        updateOutput();
    }

    function generateGrammar() {
        if (!currentInput) {
            alert('Please generate a vocabulary first!');
            return;
        }
        currentGrammar = generateGrammarRules(currentInput.length).join('<br>');
        updateOutput();
    }

    function generateNewGrammar() {
        if (!currentInput) {
            alert('Please generate a vocabulary first!');
            return;
        }
        currentGrammar = generateGrammarRules(currentInput.length).join('<br>');
        updateOutput();
    }

    function generateRandomString() {
        const length = 64; // Set length of the random string to 64
        const includeNumbers = document.getElementById('includeNumbers').checked; // Check the state of the checkbox
        let characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz.-'; // Include a-z, A-Z, ., and -

        if (includeNumbers) {
            characters += '0123456789'; // If checked, append digits
        }

        let randomString = '';

        for (let i = 0; i < length; i++) {
            randomString += characters.charAt(Math.floor(Math.random() * characters.length));
        }

        document.getElementById('inputString').value = randomString;
        currentInput = randomString; // Set currentInput to new random string
        generateWords(); // Automatically generate vocabulary for the new string
    }

    function updateOutput() {
        const outputDiv = document.getElementById('output');
        outputDiv.innerHTML = ''; // Clear previous output

        // Display flag for the vocabulary
        const flagCanvas = document.createElement('canvas');
        flagCanvas.width = 200;
        flagCanvas.height = 100;

        const ctx = flagCanvas.getContext('2d');
        // Fill the canvas with a random background color
        const backgroundColor = `rgb(${Math.floor(Math.random() * 256)}, ${Math.floor(Math.random() * 256)}, ${Math.floor(Math.random() * 256)})`;
        ctx.fillStyle = backgroundColor;
        ctx.fillRect(0, 0, flagCanvas.width, flagCanvas.height);

        // Get the rectangle count from the input field
        const rectangleCount = parseInt(document.getElementById('rectangleCount').value, 10) || 2000;

        // Draw rectangles
        for (let i = 0; i < rectangleCount; i++) {
            const width = Math.floor(Math.random() * 50) + 5; // Random width between 5 and 55
            const height = Math.floor(Math.random() * 50) + 5; // Random height between 5 and 55
            const x = Math.floor(Math.random() * (flagCanvas.width - width)); // Random x position
            const y = Math.floor(Math.random() * (flagCanvas.height - height)); // Random y position

            // Generate a random color
            const color = `rgb(${Math.floor(Math.random() * 256)}, ${Math.floor(Math.random() * 256)}, ${Math.floor(Math.random() * 256)})`;
            ctx.fillStyle = color;
            ctx.fillRect(x, y, width, height); // Draw the rectangle
        }

        const flagContainer = document.createElement('div');
        flagContainer.className = 'vocabulary-container';
        flagContainer.appendChild(flagCanvas);
        outputDiv.appendChild(flagContainer);

        // Display current vocabulary with glosses
        const vocabularyList = document.createElement('div');
        vocabularyList.innerHTML = '<h2>Current Vocabulary:</h2>';
        currentVocabulary.forEach(word => {
            const wordItem = document.createElement('p');
            // Each word gets an English gloss and a random word from the randomWords array in parentheses
            const gloss = translationMap[word] || "No translation available"; // Lookup in the mock translation map
            const randomWord = randomWords[Math.floor(Math.random() * randomWords.length)];
            wordItem.textContent = `${word} - ${gloss} (${randomWord})`;
            vocabularyList.appendChild(wordItem);
        });
        outputDiv.appendChild(vocabularyList);

        // Display current grammar rules
        if (currentGrammar) {
            const grammarSection = document.createElement('div');
            grammarSection.className = 'output-section';
            grammarSection.innerHTML = `
                <h2>Current Grammar Rules:</h2>
                <p>${currentGrammar}</p>
            `;
            outputDiv.appendChild(grammarSection);
        }
    }

    function generateExampleSentences() {
        const sentences = [];
        for (let i = 0; i < 10; i++) {
            const word1 = currentVocabulary[Math.floor(Math.random() * currentVocabulary.length)];
            const word2 = currentVocabulary[Math.floor(Math.random() * currentVocabulary.length)];
            const structure = sentenceStructures[Math.floor(Math.random() * sentenceStructures.length)];
            const sentence = structure.replace(/{word1}/g, word1).replace(/{word2}/g, word2);
            sentences.push(sentence);
        }

        const sentencesSection = document.createElement('div');
        sentencesSection.className = 'output-section';
        sentencesSection.innerHTML = `
            <h2>Example Sentences:</h2>
            <p>${sentences.join('<br>')}</p>
        `;
        document.getElementById('output').appendChild(sentencesSection);
    }

    function generateGrammarRules(length) {
        const rules = [];
        for (let i = 1; i <= length; i++) {
            rules.push(`S -> NP VP | S Conj S`);
            rules.push(`NP -> Det N | N`);
            rules.push(`VP -> V NP | V NP PP`);
            rules.push(`PP -> P NP`);
        }
        return rules;
    }
</script>

