        #inputArea {
            width: 35%;
            padding: 10px;
            border-right: 1px solid #ccc;
            display: flex;
            flex-direction: column;
        }
        #codeOutput {
            width: 35%;
            padding: 10px;
            background-color: #f9f9f9;
            overflow-y: auto;
        }
        #previewArea {
            width: 30%;
            padding: 10px;
            border-left: 1px solid #ccc;
        }
        textarea {
            width: 100%;
