<body>

<div id="container">
    <div id="inputArea">
        <h2>Input Prompt</h2>
        <textarea id="promptInput" placeholder="Enter your prompt here..."></textarea>
        <button id="generateButton">Generate Code</button>
    </div>
    <div id="codeOutput">
        <h2>Code Output</h2>
        <div id="codeOutputCode">Generated code will appear here...</div>
    </div>
    <div id="previewArea">
        <h2>Preview</h2>
        <iframe id="previewFrame" style="width: 100%; height: 100%; border: none;"></iframe>
    </div>
</div>

<script>
    document.getElementById('generateButton').addEventListener('click', async function() {
        const prompt = document.getElementById('promptInput').value;
        if (!prompt) {
            return alert("Please enter a prompt.");
        }

        try {
            const response = await fetch('https://g4f.cloud.mattf.one/api/completions', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    messages: [{ role: 'user', content: prompt }]
                })
            });

            if (!response.ok) {
                const error = await response.text();
                return alert("Error: " + error);
            }

            const data = await response.json();
            const code = data.completion ? data.completion.content : "No code generated.";  // Use content from the completion object
            document.getElementById('codeOutputCode').textContent = code;

            // Update the preview frame with the new code
            const previewFrame = document.getElementById('previewFrame');
            const doc = previewFrame.contentDocument || previewFrame.contentWindow.document;
            doc.open();
            doc.write(code);
            doc.close();
        } catch (error) {
            alert("Network error: " + error);
        }
    });
</script>

