        body { margin: 0; }
        #chat-window {
            position: absolute; 
            bottom: 20px; 
            left: 20px; 
            width: 300px; 
            background: rgba(0, 0, 0, 0.7); 
            color: white; 
            padding: 10px; 
            border-radius: 10px;
            font-family: Arial, sans-serif;
            font-size: 14px;
            display: none; 
            z-index: 999; /* Ensure it appears above other content */
        }
    </style>
</head>
<body>
    <div id="chat-window"></div>
    <a-scene>
        <a-sky color="#87CEEB"></a-sky>

        <!-- Building -->
        <a-box position="0 5 -10" depth="4" height="20" width="10" color="#8B8B8B" shadow></a-box>

        <!-- Windows on the building -->
        <script>
            const windowCount = 64;
            const buildingHeight = 20;
            const windowWidth = 0.5;
            const windowHeight = 1.5;
            const windowSpacing = 1;

            for (let i = 0; i < windowCount; i++) {
                const x = (i % 4) * (windowWidth + windowSpacing) - 2; // 4 windows per side
                const y = Math.floor(i / 4) * (windowHeight + windowSpacing) + 1; // Adjust height
                const z = -10; // Depth position

                document.write(`<a-plane position="${x} ${y} ${z}" width="${windowWidth}" height="${windowHeight}" color="#FFFFFF" shadow></a-plane>`);
            }
        </script>

        <a-plane position="0 -0.1 -10" rotation="-90 0 0" width="30" height="30" color="#7BC8A4"></a-plane>

        <a-entity position="0 1.5 -5">
            <a-text value="Loading chat..." align="center"></a-text>
        </a-entity>
    </a-scene>

    <script>
        const chatMessages = [
            { character: "Gary", message: "So, how do you fry an egg?" },
