<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Heidegger's Rainbow Candy Quest</title>
    <style>
        body {
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
            font-family: Arial, sans-serif;
            background-color: #f0f0f0;
        }
        #gameContainer {
            text-align: center;
        }
        #gameCanvas {
            border: 2px solid #333;
            background-color: #87CEEB;
        }
        #score {
            font-size: 18px;
            margin-top: 10px;
            font-weight: bold;
        }
        #message {
            font-size: 20px;
            color: #FF4500;
            margin-top: 10px;
            min-height: 30px;
        }
        #instructions {
            margin-top: 20px;
            font-size: 14px;
            color: #555;
        }
    </style>
</head>
<body>
    <div id="gameContainer">
        <h1>Heidegger's Rainbow Candy Quest</h1>
        <canvas id="gameCanvas" width="600" height="400"></canvas>
        <div id="score">Candies Collected: 0/10</div>
        <div id="message"></div>
        <div id="instructions">
            Use arrow keys to move Martin Heidegger and collect 10 rainbow candies. Dasein awaits!
        </div>
    </div>
    <script>
        const canvas = document.getElementById('gameCanvas');
        const ctx = canvas.getContext('2d');
        const scoreDiv = document.getElementById('score');
        const messageDiv = document.getElementById('message');

        let player = { x: 300, y: 200, size: 20, color: 'brown' }; // Martin Heidegger as a brown square
        let candies = [];
        let collected = 0;
        let keys = {};

        // Generate 10 rainbow candies at random positions
        for (let i = 0; i < 10; i++) {
            candies.push({
                x: Math.random() * (canvas.width - 20),
                y: Math.random() * (canvas.height - 20),
                size: 15,
                color: `hsl(${i * 36}, 100%, 50%)` // Rainbow colors
            });
        }

        // Handle key presses
        document.addEventListener('keydown', (e) => {
            keys[e.key] = true;
        });
        document.addEventListener('keyup', (e) => {
            keys[e.key] = false;
        });

        function update() {
            // Move player based on keys
            if (keys['ArrowUp']) player.y -= 3;
            if (keys['ArrowDown']) player.y += 3;
            if (keys['ArrowLeft']) player.x -= 3;
            if (keys['ArrowRight']) player.x += 3;

            // Keep player in bounds
            player.x = Math.max(0, Math.min(canvas.width - player.size, player.x));
            player.y = Math.max(0, Math.min(canvas.height - player.size, player.y));

            // Check for candy collection
            candies.forEach((candy, index) => {
                if (
                    player.x < candy.x + candy.size &&
                    player.x + player.size > candy.x &&
                    player.y < candy.y + candy.size &&
                    player.y + player.size > candy.y
                ) {
                    candies.splice(index, 1);
                    collected++;
                    scoreDiv.textContent = `Candies Collected: ${collected}/10`;
                    if (collected === 10) {
                        messageDiv.textContent = 'Victory! Dasein, throwness, and candy collected. Heidegger would be proud!';
                    }
                }
            });
        }

        function draw() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Draw candies
            candies.forEach(candy => {
                ctx.fillStyle = candy.color;
                ctx.fillRect(candy.x, candy.y, candy.size, candy.size);
                // Add a little glow effect by drawing a lighter outline
                ctx.strokeStyle = 'white';
                ctx.lineWidth = 2;
                ctx.strokeRect(candy.x, candy.y, candy.size, candy.size);
            });

            // Draw player (Heidegger)
            ctx.fillStyle = player.color;
            ctx.fillRect(player.x, player.y, player.size, player.size);
            ctx.fillStyle = 'white';
            ctx.font = '12px Arial';
            ctx.fillText('H', player.x + 5, player.y + 15); // 'H' for Heidegger
        }

        function gameLoop() {
            update();
            draw();
            requestAnimationFrame(gameLoop);
        }

        gameLoop();
    </script>
</body>
</html>
