function drawImageInHexagonCircle() {
  const centerX = canvas.width / 2;
  const centerY = canvas.height / 2;
  const hexagonRadius = 200;

  // Clear the canvas
  ctx.clearRect(0, 0, canvas.width, canvas.height);

  // Draw the hexagon
  ctx.beginPath();
  for (let i = 0; i < 6; i++) {
    const angle = (Math.PI / 3) * i;
    const x = centerX + hexagonRadius * Math.cos(angle);
    const y = centerY + hexagonRadius * Math.sin(angle);
    ctx.lineTo(x, y);
  }
  ctx.closePath();
  ctx.stroke(); // Draw hexagon outline

  // Select a random edge
  const randomEdgeIndex = Math.floor(Math.random() * 6);

  // Calculate the start and end points of the random edge
  const startX = centerX + hexagonRadius * Math.cos((Math.PI / 3) * randomEdgeIndex);
  const startY = centerY + hexagonRadius * Math.sin((Math.PI / 3) * randomEdgeIndex);
  const endX = centerX + hexagonRadius * Math.cos((Math.PI / 3) * (randomEdgeIndex + 1));
  const endY = centerY + hexagonRadius * Math.sin((Math.PI / 3) * (randomEdgeIndex + 1));

  // Calculate the midpoint of the edge (center of the circle)
  const circleCenterX = (startX + endX) / 2;
  const circleCenterY = (startY + endY) / 2;

  // Calculate the radius of the circle (half the edge length)
  const circleRadius = Math.sqrt(Math.pow(endX - startX, 2) + Math.pow(endY - startY, 2)) / 2;

  // Draw the circle
  ctx.beginPath();
  ctx.arc(circleCenterX, circleCenterY, circleRadius, 0, 2 * Math.PI);

  // Clip the circle to the hexagon
  ctx.clip();

  // Draw the image inside the clipped circle
    const imageWidth = circleRadius * 2;
    const imageHeight = circleRadius * 2;

    // Calculate the top-left corner of the image so that the center of the image aligns with the center of the circle
    const imageX = circleCenterX - imageWidth / 2;
    const imageY = circleCenterY - imageHeight / 2;

    ctx.drawImage(img, imageX, imageY, imageWidth, imageHeight);


  // Restore the canvas state
  ctx.restore();

  // Redraw the hexagon outline over the image for clarity
  ctx.beginPath();
  for (let i = 0; i < 6; i++) {
    const angle = (Math.PI / 3) * i;
    const x = centerX + hexagonRadius * Math.cos(angle);
    const y = centerY + hexagonRadius * Math.sin(angle);
    ctx.lineTo(x, y);
  }
  ctx.closePath();
  ctx.stroke(); // Redraw hexagon outline
}
</script>

</body>
</html>
