<!DOCTYPE html>
<html>
<head>
<title>IFZ ASCII Art</title>
<style>
body {
  font-family: monospace;
  background-color: #f0f0f0;
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  margin: 0;
}

pre {
  font-size: 20px;
  color: #333;
}
</style>
</head>
<body>

<pre id="asciiArt">
  IIIIIII   FFFFFFF   ZZZZZZZ
     I      F           Z
     I      FFFFFFF      Z
     I      F           Z
  IIIIIII   F        ZZZZZZZ
</pre>

<script>
  // Optional: You can add some dynamic behavior here if needed.
  // For example, change the color of the text on a timer.

  function getRandomColor() {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  }

  function changeColor() {
    const asciiArtElement = document.getElementById("asciiArt");
    asciiArtElement.style.color = getRandomColor();
  }

  // setInterval(changeColor, 2000); // Change color every 2 seconds
</script>

</body>
</html>
