```html
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Infinite Emoji Explosion</title>
    <style>
        body {
            display: flex;
            justify-content: center;
            align-items: center;
            flex-wrap: wrap;
            height: 100vh;
            background-color: #282c34;
            margin: 0;
            font-size: 2rem;
            color: white;
        }
        .emoji {
            transition: transform 0.5s; 
            margin: 5px;
        }
    </style>
</head>
<body>

<div id="emoji-container"></div>

<script>
    const emojis = ['ðŸŽ‰', 'ðŸ’¥', 'ðŸŒŸ', 'ðŸš€', 'ðŸ”¥', 'ðŸ€', 'ðŸŽˆ', 'ðŸŒˆ', 'âœ¨', 'ðŸŒ»'];
    const container = document.getElementById("emoji-container");

    function getRandomEmoji() {
        return emojis[Math.floor(Math.random() * emojis.length)];
    }

    function explodeEmojis() {
        let currentCount = 1;

        // Add the initial emoji
        container.innerHTML += getRandomEmoji();

        const interval = setInterval(() => {
            // Double the emojis on every tick
            for (let i = 0; i < currentCount; i++) {
                container.innerHTML += getRandomEmoji();
