    </a-scene>

    <canvas id="canvas" width="16" height="16"></canvas>

    <script>
        const canvas = document.getElementById('canvas');
        const ctx = canvas.getContext('2d');
        const imageData = ctx.createImageData(16, 16);

        function mandelbrot(x, y, maxIter) {
            let zx = 0, zy = 0, i = 0;
            while (zx * zx + zy * zy < 4 && i < maxIter) {
                const tmp = zx * zx - zy * zy + x;
                zy = 2.0 * zx * zy + y;
                zx = tmp;
                i++;
            }
            return i;
        }

        function generateMandelbrot() {
            const maxIter = 100;
            const zoomFactor = 3.5 / 16; // Adjust based on the desired Mandelbrot area
            const centerX = -2.5; // Center of the Mandelbrot set X
            const centerY = 1;   // Center of the Mandelbrot set Y

            for (let x = 0; x < 16; x++) {
                for (let y = 0; y < 16; y++) {
                    let mx = centerX + (x - 8) * zoomFactor;
                    let my = centerY + (y - 8) * zoomFactor;
                    const value = mandelbrot(mx, my, maxIter);
                    const color = value === maxIter ? 0 : (value % 16) * 16;
                    const index = (x + y * 16) * 4;
                    imageData.data[index] = color;     // R
                    imageData.data[index + 1] = color; // G
                    imageData.data[index + 2] = color; // B
                    imageData.data[index + 3] = 255;    // A
                }
            }
            ctx.putImageData(imageData, 0, 0);
            const texture = document.getElementById('mandelbrotTexture');
            texture.src = canvas.toDataURL();
        }

        // Initiate the Mandelbrot calculation and apply the texture
        generateMandelbrot();

        // Create a new texture asset
        document.querySelector('a-assets').innerHTML += '<img id="mandelbrotTexture" src="" />';

        // Apply the texture to the cube
        const cube = document.getElementById('mandelbrot-cube');
        cube.setAttribute('material', 'src: #mandelbrotTexture');
