                } else if (currCode in dict) {
                    currChar = dict[currCode];
                } else if (currCode === code) {
                    currChar = oldPhrase + oldPhrase.charAt(0);
                } else {
                    throw new Error('Invalid compressed code');
                }

                output += currChar;
                dict[code++] = oldPhrase + currChar;
                oldPhrase = currChar;
            }

