      ctx.fillRect(this.width / 6, -this.height / 3, this.width / 6, this.height / 8);

      // wheels
      ctx.fillStyle = "#222";
      ctx.fillRect(-this.width / 2 + 5, this.height / 2 - 10, 10, 5);
      ctx.fillRect(this.width / 2 - 15, this.height / 2 - 10, 10, 5);

      ctx.restore();
    }
  }

  function drawPlayer(ctx, x, y) {
    ctx.save();
    ctx.translate(x, y);

