  function resetGame() {
    obstacles = [];
    crashes = 0;
    score = 0;
    displayedSpeed = 0;
    targetSpeed = 0;
    recoveryStartTime = null;
    player.x = PLAYER_START_X;
    player.y = PLAYER_START_Y;
    gameOver = false;
    ui.style.display = "block";
    message.style.display = "none";
    lastSpawnTime = 0;
    frames = 0;
    roadScroll = 0;
    requestAnimationFrame(gameLoop);
  }

