<div id="content">
  <h1>Rizz Brainrot Sigma Ohio Generator</h1>
  <button onclick="generatePhrase()">Generate!</button>
  <p id="output"></p>
</div>

<script>
function generatePhrase() {
  const rizzPhrases = [
    "Rizzly bear with the stare.",
    "Dropping rizz bombs, handle with caution.",
    "Overflowing with that unspoken rizz.",
    "Certified rizzler, proceed accordingly.",
    "Weaponized rizz engagement initiated."
  ];

  const brainrotPhrases = [
    "Brain so fried, I'm speaking in memes.",
    "Currently experiencing peak brainrot levels.",
    "My brain cells are playing skibidi toilet remixes.",
    "Warning: brain melting may occur.",
    "This is my brain on skibidi."
  ];

  const sigmaPhrases = [
    "Sigma grindset activated.",
    "Staying solo, embracing the sigma path.",
    "Sigma male energy radiating.",
    "Unbothered, thriving, sigma.",
    "Reject modernity, embrace sigma."
  ];

  const ohioPhrases = [
    "Only in Ohio...",
    "Ohio is wildin' today.",
    "Something strange is happening in Ohio.",
    "Standard Ohio shenanigans.",
    "Welcome to Ohio, where the bizarre is normal."
  ];

  const rizz = rizzPhrases[Math.floor(Math.random() * rizzPhrases.length)];
  const brainrot = brainrotPhrases[Math.floor(Math.random() * brainrotPhrases.length)];
  const sigma = sigmaPhrases[Math.floor(Math.random() * sigmaPhrases.length)];
  const ohio = ohioPhrases[Math.floor(Math.random() * ohioPhrases.length)];

  const finalPhrase = `${rizz} ${brainrot} ${sigma} ${ohio}`;
  document.getElementById("output").textContent = finalPhrase;
}
</script>

</body>
</html>
