<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Random Slop Generator</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            background-color: #f0f0f0;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            height: 100vh;
            margin: 0;
        }
        h1 {
            color: #333;
        }
        button {
            padding: 10px 20px;
            font-size: 16px;
            background-color: #4CAF50;
            color: white;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            margin-bottom: 20px;
        }
        button:hover {
            background-color: #45a049;
        }
        #slopOutput {
            max-width: 600px;
            background-color: #fff;
            border: 1px solid #ddd;
            padding: 20px;
            border-radius: 5px;
            box-shadow: 0 0 10px rgba(0,0,0,0.1);
            white-space: pre-wrap;
            font-family: monospace;
        }
        #slopOutput:empty::before {
            content: "Click 'Generate Slop' to see some messy, random content!";
            color: #999;
        }
    </style>
</head>
<body>
    <h1>Random Slop Generator</h1>
    <button onclick="generateSlop()">Generate Slop</button>
    <div id="slopOutput"></div>

    <script>
        function generateSlop() {
            const slopTypes = [
                'nonsenseText',
                'randomNumbers',
                'gibberishWords',
                'emojiMess'
            ];
            const type = slopTypes[Math.floor(Math.random() * slopTypes.length)];
            let slop = '';

            switch (type) {
                case 'nonsenseText':
                    const words = ['blorp', 'fizzle', 'quizzle', 'snort', 'wobble', 'zephyr', 'gorm', 'plop'];
                    const sentences = Math.floor(Math.random() * 10) + 5;
                    for (let i = 0; i < sentences; i++) {
                        let len = Math.floor(Math.random() * 10) + 3;
                        let sentence = '';
                        for (let j = 0; j < len; j++) {
                            sentence += words[Math.floor(Math.random() * words.length)] + ' ';
                        }
                        slop += sentence.trim() + (i % 2 === 0 ? '!' : '?') + '\n';
                    }
                    break;
                case 'randomNumbers':
                    const nums = [];
                    for (let i = 0; i < 50; i++) {
                        nums.push(Math.floor(Math.random() * 1000));
                    }
                    slop = 'Random numbers: ' + nums.join(', ') + '\n';
                    break;
                case 'gibberishWords':
                    const chars = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789!@#$%^&*( )';
                    for (let i = 0; i < 20; i++) {
                        let word = '';
                        let len = Math.floor(Math.random() * 15) + 3;
                        for (let j = 0; j < len; j++) {
                            word += chars[Math.floor(Math.random() * chars.length)];
                        }
                        slop += word + ' ';
                    }
                    slop += '\n';
                    break;
                case 'emojiMess':
                    const emojis = ['ðŸ¤–', 'ðŸš€', 'ðŸ¦–', 'ðŸ’©', 'ðŸ•', 'ðŸŒˆ', 'ðŸ”¥', 'ðŸ§Ÿ'];
                    for (let i = 0; i < 30; i++) {
                        slop += emojis[Math.floor(Math.random() * emojis.length)] + ' ';
                    }
                    slop += '\n';
                    break;
            }

            document.getElementById('slopOutput').textContent = slop;
        }
    </script>
</body>
</html>
