<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Emoji Game</title>
    <style>
        body {
            margin: 0;
            overflow: hidden;
            background-image: url('https://picsum.photos/16/16');
            background-repeat: repeat;
        }

        #gameCanvas {
            position: absolute;
            top: 0;
            left: 0;
            background: transparent;
        }

        .emoji {
            font-size: 32px;
            position: absolute;
        }

        .monster {
            font-size: 32px;
            position: absolute;
        }

        .gem {
            font-size: 32px;
            position: absolute;
        }
    </style>
</head>

<body>
    <canvas id="gameCanvas" width="800" height="600"></canvas>
    <script>
        const canvas = document.getElementById('gameCanvas');
        const ctx = canvas.getContext('2d');

        const emojiFace = 'ðŸ˜€';
        const monsters = ['ðŸ‘¾', 'ðŸ‘¹', 'ðŸ‘º', 'ðŸ‘»'];
        const gems = ['ðŸ’Ž', 'ðŸ’°'];
        let facePosition = { x: canvas.width / 2, y: canvas.height / 2 };
        let score = 0;
        let health = 100;

        function drawFace() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            ctx.fillText(emojiFace, facePosition.x, facePosition.y);
            drawMonsters();
            drawGems();
            ctx.fillText(`Score: ${score}`, 10, 20);
            ctx.fillText(`Health: ${health}`, 10, 40);
        }

        function drawMonsters() {
            monsters.forEach((monster, index) => {
                const x = Math.random() * canvas.width;
                const y = Math.random() * canvas.height;
                ctx.fillText(monster, x, y);
            });
        }

        function drawGems() {
            gems.forEach((gem, index) => {
                const x = Math.random() * canvas.width;
                const y = Math.random() * canvas.height;
                ctx.fillText(gem, x, y);
            });
        }

        function moveFace(event) {
            switch (event.key) {
                case 'y':
                    facePosition.x -= 10;
                    facePosition.y -= 10;
                    break;
                case 'u':
                    facePosition.x += 10;
                    facePosition.y -= 10;
                    break;
                case 'g':
                    facePosition.x -= 10;
                    break;
                case 'h':
                    // Pass action (do nothing)
                    break;
                case 'j':
                    facePosition.x += 10;
                    break;
                case 'b':
                    facePosition.x -= 10;
                    facePosition.y += 10;
                    break;
                case 'n':
                    facePosition.x += 10;
                    facePosition.y += 10;
                    break;
            }
            drawFace();
        }

        window.addEventListener('keydown', moveFace);
        drawFace();
    </script>
</body>

</html>
