    <title>Emoji Control</title>
    <style>
        body {
            margin: 0;
            padding: 0;
            background-image: url('https://picsum.photos/200/300');
            background-size: cover;
            height: 100vh;
            display: flex;
            justify-content: flex-start;
            align-items: flex-start;
        }

        #emoji {
            font-size: 50px;
            position: absolute;
            top: 0;
            left: 0;
            transition: top 0.1s, left 0.1s;
        }
    </style>
</head>

<body>
    <div id="emoji">üòÄ</div>

    <script>
        const emoji = document.getElementById('emoji');
        let position = { top: 0, left: 0 };

        document.addEventListener('keydown', (event) => {
            switch (event.key) {
                case 'y': // up left
                    position.top -= 10;
                    position.left -= 10;
                    break;
                case 'u': // up right
                    position.top -= 10;
                    position.left += 10;
                    break;
                case 'g': // left
                    position.left -= 10;
                    break;
                case 'h': // pass
                    break;
                case 'j': // right
                    position.left += 10;
                    break;
                case 'b': // down left
                    position.top += 10;
                    position.left -= 10;
                    break;
                case 'n': // down right
                    position.top += 10;
                    position.left += 10;
                    break;
            }
            updatePosition();
        });

        function updatePosition() {
            emoji.style.top = position.top + 'px';
            emoji.style.left = position.left + 'px';
        }
    </script>
</body>

</html>
