```html
<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Emoji Control with Monsters and Items</title>
    <style>
        body {
            margin: 0;
            padding: 0;
            background-image: url('https://picsum.photos/200/300');
            background-size: cover;
            height: 100vh;
            position: relative;
            overflow: hidden;
        }

        #emoji {
            font-size: 50px;
            position: absolute;
            top: 0;
            left: 0;
            transition: top 0.1s, left 0.1s;
        }

        .random-emoji {
            font-size: 30px;
            position: absolute;
        }
    </style>
</head>

<body>
    <div id="emoji">üòÄ</div>

    <script>
        const emoji = document.getElementById('emoji');
        let position = { top: 0, left: 0 };

        // Function to generate random emojis
        function createRandomEmoji() {
            const emojis = ['üëæ', 'üéÆ', 'üßô‚Äç‚ôÇÔ∏è', 'üßö', 'ü¶Ñ', 'üçÑ', '‚öîÔ∏è', 'üõ°Ô∏è'];
            const randomEmoji = emojis[Math.floor(Math.random() * emojis.length)];
            const randomX = Math.random() * (window.innerWidth - 50);
            const randomY = Math.random() * (window.innerHeight - 50);

            const emojiElement = document.createElement('div');
            emojiElement.className = 'random-emoji';
            emojiElement.innerText = randomEmoji;
            emojiElement.style.left = randomX + 'px';
            emojiElement.style.top = randomY + 'px';

            document.body.appendChild(emojiElement);
        }

        // Create multiple random emojis
        for (let i = 0; i < 10; i++) {
            createRandomEmoji();
        }

        document.addEventListener('keydown', (event) => {
            switch (event.key) {
                case 'y': // up left
                    position.top -= 10;
                    position.left -= 10;
                    break;
                case 'u': // up right
                    position.top -= 10;
                    position.left += 10;
                    break;
                case 'g': // left
                    position.left -= 10;
                    break;
                case 'h': // pass
                    break;
                case 'j': // right
                    position.left += 10;
                    break;
                case 'b': // down left
                    position.top += 10;
                    position.left -= 10;
                    break;
                case 'n': // down right
                    position.top += 10;
                    position.left += 10;
                    break;
            }
            updatePosition();
        });

        function updatePosition() {
            emoji.style.top = position.top + 'px';
            emoji.style.left = position.left + 'px';
        }
    </script>
</body>

</html>
