                for (let y = 0; y < tileSize; y++) {
                    tileCtx.fillStyle = colors[Math.floor(Math.random() * colors.length)];
                    tileCtx.fillRect(x, y, 1, 1);
                }
            }
            tiles.push(tileCanvas);
        }

